import React from "react";
import {useState, useEffect} from "react";
import {Link} from "react-router-dom";
import {db} from "./firebase";


export const Add_kitchen = () => {
    const [form, setForm] = useState({ firstArea: "", secondArea: "", thirdArea: "" });
    const [errors, setError] = useState([]);
    const [success, setSuccess] = useState("")
    const handleAddToKitchen = (e) => {
        const { name, value } = e.target;
        setForm(prev => {
            return {
                ...prev,
                [name]: value
            }
        })
    };
    /////////////////////////////////////
    const handleSubmit = (e) => {
        e.preventDefault();
        const errorArray = [];
        if (form.firstArea.length < 2) {
            errorArray.push("pierwsze pole musi zwierac conajmniej 2 znaki");
        }
        if (form.secondArea.length > 120 || form.secondArea.length < 2) {
            errorArray.push("drugie pole musi zawierać od 2 do 120 znaków");
        }
        if (form.thirdArea.length > 120 || form.thirdArea.length < 2) {
            errorArray.push("trzecie pole musi zawierać od 2 do 120 znaków");
        }
        setError(errorArray);
        if (errorArray.length === 0) {
            db.collection("gratitude").add({
                date: startDate.toLocaleDateString(),
                firstGratitude: form.firstArea,
                secondGratitude: form.secondArea,
                thirdGratitude: form.thirdArea
            })
                .then((docRef) => {
                    console.log("Document written with ID: ", docRef.id);
                    setForm({ firstArea: "", secondArea: "", thirdArea: "", });
                    setSuccess("Wdzięczność została dodana!")
                })
                .catch((error) => {
                    console.error("Error adding document: ", error);
                });
        }
    }


    return (
        <div className="main_container">
            <div className="title">
                <h1>Dodaj do Kuchni</h1>
            </div>
            <div className="add_content">
                <form className="" onSubmit={handleSubmit}>
                <h2>Nazwa</h2>
                <input type="text" className="input" value={form.firstArea} onChange={handleAddToKitchen} name="firstArea"/>
                <h2>Ilość</h2>
                <input type="text" className="input" value={form.secondArea} onChange={handleAddToKitchen} name="secondArea"/>
                    <select name="unit" className="select" value={form.thirdArea} onChange={handleAddToKitchen} name="thirdArea">
                        <option>sztuk</option>
                        <option>opakowań</option>
                        <option>gram</option>
                        <option>kg</option>
                        <option>ml</option>
                        <option>l</option>
                    </select>
                </form>
            </div>
            <div className="buttons">
                <button className="btn">Dodaj</button>
                <Link to="/Kitchen" className="btn">Powrót</Link>
                <Link to="/" className="btn">Mój Dom</Link>
            </div>
        </div>
    )
}
