import React from "react";
import {useState, } from "react";
import {Link} from "react-router-dom";
import {db} from "./firebase";


export const Add_kitchen = () => {
    const [form, setForm] = useState({ firstArea: "", secondArea: "", thirdArea: "" });
    const [errors, setError] = useState([]);
    const [success, setSuccess] = useState("")
    const handleAddToKitchen = (e) => {
        const { name, value } = e.target;
        setForm(prev => {
            return {
                ...prev,
                [name]: value
            }
        })
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const errorArray = [];
        if (form.firstArea.length < 2) {
            errorArray.push("pierwsze pole musi zwierac conajmniej 2 znaki");
        }
        if (form.secondArea.length < 1) {
            errorArray.push("drugie pole nie może być puste!");
        }
        setError(errorArray);
        if (errorArray.length === 0) {
            db.collection("products").add({
                title: form.firstArea,
                quantity: form.secondArea,
                unit: form.thirdArea === "" ? "sztuk" : form.thirdArea
            })
                .then((docRef) => {
                    console.log("Document written with ID: ", docRef.id);
                    setForm({ firstArea: "", secondArea: "", thirdArea: "", });
                    setSuccess("Dodano do listy ")
                })
                .catch((error) => {
                    console.error("Error adding document: ", error);
                });
        }
    }


    return (
        <div className="main_container">
            <div className="title">
                <h1 className="header">Dodaj do Kuchni</h1>
            </div>
            <div className="add_content">

                <form className="form_add" onSubmit={handleSubmit}>
                    <div className="form_errors">{errors.length > 0 && errors.map((error, key) => <p key={key} style={{ color: "red", margin: "0px" }}>{error}</p>)}<p style={{ color: "green", margin: "0px" }}>{errors.length === 0 && success}</p></div>
                <h2>Nazwa</h2>
                <input type="text" className="input" value={form.firstArea} onChange={handleAddToKitchen} name="firstArea"/>
                <h2>Ilość</h2>
                <input type="text" className="input" value={form.secondArea} onChange={handleAddToKitchen} name="secondArea"/>
                    <select name="unit" className="select"  value={form.thirdArea} onChange={handleAddToKitchen} name="thirdArea">
                        <option value="sztuk" >sztuk</option>
                        <option value="opakowań">opakowań</option>
                        <option value="gram">gram</option>
                        <option value="kg">kg</option>
                        <option value="ml">ml</option>
                        <option value="l">l</option>
                    </select>
                    <div className="buttons">
                    <button type=" submit" className="btn">Dodaj</button>
                    </div>
                </form>
            </div>
            <div className="buttons">
              {/*<button type="submit" className="btn">Dodaj</button>*/}
                <Link to="/Kitchen" className="btn">Powrót</Link>
                <Link to="/" className="btn">Mój Dom</Link>
            </div>
        </div>
    )
}
