import React from "react";
import {Link} from "react-router-dom";
import {useState, } from "react";
import {db} from "./firebase";

export const Add_industry = () => {
    const [form, setForm] = useState({ firstArea: "", secondArea: "", thirdArea: "" });
    const [errors, setError] = useState([]);
    const [success, setSuccess] = useState("")
    const handleAddToIndustry = (e) => {
        const { name, value } = e.target;
        setForm(prev => {
            return {
                ...prev,
                [name]: value
            }
        })
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const errorArray = [];
        if (form.firstArea.length < 2) {
            errorArray.push("pierwsze pole musi zwierac conajmniej 2 znaki");
        }
        if (form.secondArea.length < 1) {
            errorArray.push("drugie pole nie może być puste!");
        }
        setError(errorArray);
        if (errorArray.length === 0) {
            db.collection("industry").add({
                title: form.firstArea,
                quantity: form.secondArea,
                unit: form.thirdArea === "" ? "sztuk" : form.thirdArea
            })
                .then((docRef) => {
                    console.log("Document written with ID: ", docRef.id);
                    setForm({ firstArea: "", secondArea: "", thirdArea: "", });
                    setSuccess("Dodano do listy ")
                })
                .catch((error) => {
                    console.error("Error adding document: ", error);
                });
        }
    }


    return (
        <div className="main_container">
            <div className="title">
                <h1>Dodaj do Kuchni</h1>
            </div>
            <div className="add_content">
                <div className="form__errors">{errors.length > 0 && errors.map((error, key) => <p key={key} style={{ color: "red" }}>{error}</p>)}<p style={{ color: "green" }}>{errors.length === 0 && success}</p></div>
                <form className="" onSubmit={handleSubmit}>
                    <h2>Nazwa</h2>
                    <input type="text" className="input" value={form.firstArea} onChange={handleAddToIndustry} name="firstArea"/>
                    <h2>Ilość</h2>
                    <input type="text" className="input" value={form.secondArea} onChange={handleAddToIndustry} name="secondArea"/>
                    <select name="unit" className="select"  value={form.thirdArea} onChange={handleAddToIndustry} name="thirdArea">
                        <option value="sztuk" >sztuk</option>
                        <option value="opakowań">opakowań</option>
                        <option value="gram">gram</option>
                        <option value="kg">kg</option>
                        <option value="ml">ml</option>
                        <option value="l">l</option>
                    </select>
                    <div className="buttons">
                        <button type="submit" className="btn">Dodaj</button>
                    </div>
                </form>
            </div>
            <div className="buttons">
                {/*<button type="submit" className="btn">Dodaj</button>*/}
                <Link to="/Kitchen" className="btn">Powrót</Link>
                <Link to="/" className="btn">Mój Dom</Link>
            </div>
        </div>
    )
}







