import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import {db} from "./firebase"


export const Kitchen = () => {
    const [cards, setCards] = useState([]);
    const [edit, setEdit] = useState(false);
    const [editFirst, setEditFirst] = useState("");
    const [editSecond, setEditSecond] = useState("");
    const [editThird, setEditThird] = useState("");
    const [errors, setErrors] = useState([]);
    const [success, setSuccess] = useState("")

    useEffect(() => {
        db.collection("products").get()
            .then((querySnapshot) => {
                const allProducts = [];
                querySnapshot.forEach((doc) => {
                    allProducts.push({
                        ...doc.data(),
                        id: doc.id,
                    });
                });
                setCards(allProducts)
            });
    }, [edit]);

    const handleDelete = (id) => {
        db.collection("products").doc(id).delete().then(() => {
            const deleteCard = cards.filter(card => card.id !== id)
            setCards(deleteCard);
        }).catch((error) => {
            console.error("Error removing document: ", error);
        });
    }
    const handleEdit = (id, firstProduct, secondProduct, thirdProduct) => {
        setEdit(id);
        setEditFirst(firstProduct);
        setEditSecond(secondProduct);
        setEditThird(thirdProduct);
    }
    const handleUpdate = (e) => {
        e.preventDefault()
        const errorArray = [];
        if (editFirst.length < 2) {
            errorArray.push("pierwsze pole musi zwierac conajmniej 2 znaki");
        }
        if (editSecond.length < 1) {
            errorArray.push("drugie pole musi nie może być puste !");
        }
        if (editThird.length > 120 || editThird.length < 2) {
            errorArray.push("trzecie pole musi zawierać od 2 do 120 znaków");
        }
        setErrors(errorArray);
        if (errorArray.length === 0) {
            db.collection("products").doc(edit).update({
                firstProduct: editFirst,
                secondProduct: editSecond,
                thirdProduct: editThird,
            })
                .then((docRef) => {
                    setEdit(false)
                })
                .catch((error) => {
                    console.error("Error adding document: ", error);
                });
        }
    }
    const handleBack = () => {
        setEdit(false)
    }
    // // moje wyświetlanie w konsoli
    // useEffect(() => {
    //     db.collection("products").get().then((snapshot => {
    //         snapshot.forEach(doc => {
    //            console.log(doc.data())
    //         });
    //     }))
    // }, []);

    return (
        <>
            <div className="main_container">
                <div className="title">
                    <h1>Stan Kuchni</h1>
                </div>
                <div className="main_content">
                    <table className="table">
                        <thead>
                        <th className="table_title">Nazwa</th>
                        <th className="table_quantity">Ilość</th>
                        <th className="table_action">Akcja</th>
                        </thead>
                        <tbody>
                        {cards.map(({firstProduct, secondProduct, thirdProduct, id}) => {
                            return (
                                <tr key={id}>
                                    <td className="table_title">{`${firstProduct}`}</td>
                                    <td className="table_quantity">{`${secondProduct}${thirdProduct}`}</td>
                                    <td>
                                        <button onClick={() => handleDelete(id)}>
                                            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24"
                                                 viewBox="0 0 24 24" stroke="#FFB03B" fill="#FFB03B">
                                                <path
                                                    d="M18 14.45v6.55h-16v-12h6.743l1.978-2h-10.721v16h20v-10.573l-2 2.023zm1.473-10.615l1.707 1.707-9.281 9.378-2.23.472.512-2.169 9.292-9.388zm-.008-2.835l-11.104 11.216-1.361 5.784 5.898-1.248 11.103-11.218-4.536-4.534z"></path>
                                            </svg>
                                        </button>
                                        <button
                                            onClick={() => handleEdit(id, firstProduct, secondProduct, thirdProduct)}>
                                            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24"
                                                 viewBox="0 0 24 24" stroke="#BD4932" fill="#BD4932">
                                                <path
                                                    d="M9 19c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm5-17v2h-20v-2h5.711c.9 0 1.631-1.099 1.631-2h5.315c0 .901.73 2 1.631 2h5.712zm-3 4v16h-14v-16h-2v18h18v-18h-2z"></path>
                                            </svg>
                                        </button>
                                    </td>
                                </tr>
                            )
                        })}
                            <tr>
                                <td className="table_title">Makaron</td>
                                <td className="table_quantity">2kg</td>
                                <td>
                                    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"
                                         stroke="#FFB03B" fill="#FFB03B">
                                        <path
                                            d="M18 14.45v6.55h-16v-12h6.743l1.978-2h-10.721v16h20v-10.573l-2 2.023zm1.473-10.615l1.707 1.707-9.281 9.378-2.23.472.512-2.169 9.292-9.388zm-.008-2.835l-11.104 11.216-1.361 5.784 5.898-1.248 11.103-11.218-4.536-4.534z"></path>
                                    </svg>
                                    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"
                                         stroke="#BD4932" fill="#BD4932">
                                        <path
                                            d="M9 19c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm5-17v2h-20v-2h5.711c.9 0 1.631-1.099 1.631-2h5.315c0 .901.73 2 1.631 2h5.712zm-3 4v16h-14v-16h-2v18h18v-18h-2z"></path>
                                    </svg>
                                </td>
                            </tr>
                            <tr>
                            <td className="table_title">Jajki</td>
                            <td className="table_quantity">7szt.</td>
                            <td><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" stroke="#FFB03B" fill="#FFB03B">
                            <path d="M18 14.45v6.55h-16v-12h6.743l1.978-2h-10.721v16h20v-10.573l-2 2.023zm1.473-10.615l1.707 1.707-9.281 9.378-2.23.472.512-2.169 9.292-9.388zm-.008-2.835l-11.104 11.216-1.361 5.784 5.898-1.248 11.103-11.218-4.536-4.534z"></path>
                            </svg><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" stroke="#BD4932" fill="#BD4932">
                            <path d="M9 19c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm5-17v2h-20v-2h5.711c.9 0 1.631-1.099 1.631-2h5.315c0 .901.73 2 1.631 2h5.712zm-3 4v16h-14v-16h-2v18h18v-18h-2z"></path>
                            </svg></td>
                            </tr>
                            <tr>
                            <td className="table_title">Papryka</td>
                            <td className="table_quantity">1szt.</td>
                            <td><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" stroke="#FFB03B" fill="#FFB03B">
                            <path d="M18 14.45v6.55h-16v-12h6.743l1.978-2h-10.721v16h20v-10.573l-2 2.023zm1.473-10.615l1.707 1.707-9.281 9.378-2.23.472.512-2.169 9.292-9.388zm-.008-2.835l-11.104 11.216-1.361 5.784 5.898-1.248 11.103-11.218-4.536-4.534z"></path>
                            </svg><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" stroke="#BD4932" fill="#BD4932">
                            <path d="M9 19c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm5-17v2h-20v-2h5.711c.9 0 1.631-1.099 1.631-2h5.315c0 .901.73 2 1.631 2h5.712zm-3 4v16h-14v-16h-2v18h18v-18h-2z"></path>
                            </svg></td>
                            </tr>
                            <tr>
                            <td className="table_title">Ser</td>
                            <td className="table_quantity">257g</td>
                            <td><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" stroke="#FFB03B" fill="#FFB03B">
                            <path d="M18 14.45v6.55h-16v-12h6.743l1.978-2h-10.721v16h20v-10.573l-2 2.023zm1.473-10.615l1.707 1.707-9.281 9.378-2.23.472.512-2.169 9.292-9.388zm-.008-2.835l-11.104 11.216-1.361 5.784 5.898-1.248 11.103-11.218-4.536-4.534z"></path>
                            </svg><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" stroke="#BD4932" fill="#BD4932">
                            <path d="M9 19c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm5-17v2h-20v-2h5.711c.9 0 1.631-1.099 1.631-2h5.315c0 .901.73 2 1.631 2h5.712zm-3 4v16h-14v-16h-2v18h18v-18h-2z"></path>
                            </svg></td>
                            </tr>
                            <tr>
                            <td className="table_title">Dżem</td>
                            <td className="table_quantity">Mało</td>
                            <td><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" stroke="#FFB03B" fill="#FFB03B">
                            <path d="M18 14.45v6.55h-16v-12h6.743l1.978-2h-10.721v16h20v-10.573l-2 2.023zm1.473-10.615l1.707 1.707-9.281 9.378-2.23.472.512-2.169 9.292-9.388zm-.008-2.835l-11.104 11.216-1.361 5.784 5.898-1.248 11.103-11.218-4.536-4.534z"></path>
                            </svg><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" stroke="#BD4932" fill="#BD4932">
                            <path d="M9 19c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm4 0c0 .552-.448 1-1 1s-1-.448-1-1v-10c0-.552.448-1 1-1s1 .448 1 1v10zm5-17v2h-20v-2h5.711c.9 0 1.631-1.099 1.631-2h5.315c0 .901.73 2 1.631 2h5.712zm-3 4v16h-14v-16h-2v18h18v-18h-2z"></path>
                            </svg></td>
                            </tr>
                            </tbody>
                            </table>
                            </div>
                            <div className="buttons">
                            <Link to="/Add_kitchen" className="btn">Dodaj</Link>
                            <Link to="/" className="btn">Powrót</Link>
                            </div>
                            </div>
                        {
                            edit && (
                            <div className="edit_container">
                        {errors.length > 0 && <div className="edit_error" style={{background: "red"}}>{errors.map(error => <p>{error}</p>)}</div>}
                            <form className="edit_form" onSubmit={handleUpdate}>
                            <h2 className="edit_title">edytuj produkt</h2>
                            <textarea className="edit_textarea" type="text" value={editFirst} onChange={e => setEditFirst(e.target.value)} />
                            <textarea className="edit_textarea" type="text" value={editSecond} onChange={e => setEditSecond(e.target.value)} />
                            <textarea className="edit_textarea" type="text" value={editThird} onChange={e => setEditThird(e.target.value)} />
                            <div className="edit_btnArea">
                            <button className="edit_back-btn edit_btn" onClick={handleBack}>cofnij</button>
                            <button className="edit_btn" type="submit">zapisz</button>
                            </div>
                            </form>
                            </div>)
                        }
                            </>
                            )
                            }
